<form class="contact-form" method="POST" action="/contact">{!! csrf_field() !!}<p><label for="contact-name">Họ Tên</label><input type="text" id="contact-name" name="name" required /></p><p><label for="contact-email">Email</label><input type="email" id="contact-email" name="email" required /></p><p><label for="contact-tel">Điện Thoại</label><input type="text" id="contact-tel" name="tel" /></p><p><label for="contact-msg">Tin Nhắn</label><textarea id="contact-msg" name="msg" rows="3"></textarea></p><p><label></label><input type="submit" value="Gửi Tin Nhắn" /></p></form>